(1, 0, 0, 'Главная', '', 0, 0, '', 0, '<!DOCTYPE HTML>\n<html lang=\"ru\">\n	<head>\n		[[$head]]\n	</head>\n	<body class=\"homepage\">\n		<div id=\"page-wrapper\">\n\n			<!-- Header -->\n				<div id=\"header\">\n					[[$header]]\n				</div>\n\n			<!-- Banner -->\n				<section id=\"banner\">\n					[[$banner]]\n				</section>\n\n			<!-- Carousel -->\n				<section class=\"carousel\">\n					[[$slider]]\n				</section>\n\n			<!-- Main -->\n				<div class=\"wrapper style2\">\n					[[$main]]\n				</div>\n\n			<!-- Features -->\n				<div class=\"wrapper style1\">\n					[[$features]]\n				</div>\n\n			<!-- Footer -->\n				<div id=\"footer\">\n					[[$footer]]\n				</div>\n\n		</div>\n			[[$scripts]]\n	</body>\n</html>', 0, 'a:0:{}', 0, ''),
